import React, { useEffect, useState } from "react";
import axios from "axios";

type Lab1AVariance = {
  matrices: number[][][];
};

type GeneratedLab1AVariance = {
  number: number;
  variance: Lab1AVariance;
};

type Lab1AVariant = {
  user_id: number;
  variant: GeneratedLab1AVariance;
};

const Lab1aVariance: React.FC = () => {
  const [data, setData] = useState<Lab1AVariant | null>(null);
  const [loading, setLoading] = useState<boolean>(false);
  const [error, setError] = useState<string | null>(null);

  const fetchData = async () => {
    setLoading(true);
    setError(null);

    try {
      const token =
        "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJleHAiOjE3MzQwNzI1ODIsImlhdCI6MTczNDAyOTM4MiwidXNlcl9pZCI6NCwidXNlcl9wb3N0Ijoic3R1ZGVudCJ9.HVKOr05Ejm9qEdWu1S-AJQBmrgUTu1pjADdHV8vFGjI";
      const labToken = "ajkdfnshdabvpdhifvndsfjhlnfsjdlfkjsdfsdfsdf";

      const response = await axios.get<Lab1AVariant>("http://localhost:8001/lab1a/variant", {
        headers: {
          Authorization: `Bearer ${token}`,
          "lab-token": labToken,
        },
        params: {
          laboratory_id: 1,
        },
      });

      setData(response.data);
    } catch (err: any) {
      setError(err.message || "Ошибка при выполнении запроса");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  if (loading) {
    return <div className="loading">Загрузка...</div>;
  }

  if (error) {
    return <div className="error">Ошибка: {error}</div>;
  }

  return (
    <div style={styles.container}>
      <h1 style={styles.title}>Generated Lab1A Variance</h1>
      {data ? (
        <div>
          <h2 style={styles.subtitle}>Variant Number: {data.variant.number}</h2>
          <div style={styles.matricesContainer}>
            {data.variant.variance.matrices.map((matrix, matrixIndex) => (
              <div key={matrixIndex} style={styles.matrixBlock}>
                <h3 style={styles.matrixTitle}>Matrix {matrixIndex + 1}</h3>
                <table style={styles.table}>
                  <tbody>
                    {matrix.map((row, rowIndex) => (
                      <tr key={rowIndex}>
                        {row.map((cell, cellIndex) => (
                          <td key={cellIndex} style={styles.cell}>
                            {cell}
                          </td>
                        ))}
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            ))}
          </div>
        </div>
      ) : (
        <div style={styles.noData}>Нет данных для отображения.</div>
      )}
    </div>
  );
};

export default Lab1aVariance;

const styles: { [key: string]: React.CSSProperties } = {
  container: {
    padding: "20px",
    fontFamily: "'Arial', sans-serif",
    color: "#333",
  },
  title: {
    textAlign: "center",
    marginBottom: "20px",
    fontSize: "24px",
    fontWeight: "bold",
  },
  subtitle: {
    textAlign: "center",
    marginBottom: "30px",
    fontSize: "20px",
  },
  matricesContainer: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(300px, 1fr))",
    gap: "20px",
  },
  matrixBlock: {
    background: "#f9f9f9",
    borderRadius: "8px",
    boxShadow: "0 4px 6px rgba(0, 0, 0, 0.1)",
    padding: "15px",
    textAlign: "center",
  },
  matrixTitle: {
    marginBottom: "10px",
    fontSize: "18px",
    fontWeight: "bold",
  },
  table: {
    width: "100%",
    borderCollapse: "collapse",
  },
  cell: {
    border: "1px solid #ccc",
    padding: "10px",
    textAlign: "center",
    minWidth: "50px",
    maxWidth: "50px",
    height: "50px",
  },
  noData: {
    textAlign: "center",
    fontSize: "16px",
    color: "#777",
  },
  loading: {
    textAlign: "center",
    fontSize: "18px",
    color: "#555",
  },
  error: {
    textAlign: "center",
    fontSize: "18px",
    color: "#d9534f",
  },
};
