const Lab1aVariance: React.FC = () => {
  const [data, setData] = useState<Lab1AVariant | null>(null);
  const [loading, setLoading] = useState<boolean>(false);
  const [error, setError] = useState<string | null>(null);
  const [activeStep, setActiveStep] = useState<number>(0);

  // Обновленные шаги
  const steps = ["Задание", "Задание 1", "Задание 2", "Задание 3", "Задание 4", "Задание 5", "Результат"];

  const fetchData = async () => {
    setLoading(true);
    setError(null);

    try {
      let token = localStorage.getItem("jwt-token");
      let labToken = localStorage.getItem("lab-token");

      if (!token) {
        token =
          "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJleHAiOjE3MzQwNzI1ODIsImlhdCI6MTczNDAyOTM4MiwidXNlcl9pZCI6NCwidXNlcl9wb3N0Ijoic3R1ZGVudCJ9.HVKOr05Ejm9qEdWu1S-AJQBmrgUTu1pjADdHV8vFGjI";
        localStorage.setItem("jwt-token", token);
      }

      if (!labToken) {
        labToken = "ajkdfnshdabvpdhifvndsfjhlnfsjdlfkjsdfsdfsdf";
        localStorage.setItem("lab-token", labToken);
      }

      const response = await axios.get<Lab1AVariant>("http://localhost:8001/lab1a/variant", {
        headers: {
          Authorization: `Bearer ${token}`,
          "lab-token": labToken,
        },
        params: {
          laboratory_id: 1,
        },
      });

      setData(response.data);
      localStorage.setItem("task", JSON.stringify(response.data));
    } catch (err: any) {
      setError(err.message || "Ошибка при выполнении запроса");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    const localData = localStorage.getItem("task");
    if (localData) {
      try {
        const parsedData = JSON.parse(localData) as Lab1AVariant;
        setData(parsedData);
      } catch (err) {
        console.error("Ошибка при разборе данных из localStorage:", err);
        localStorage.removeItem("task");
        fetchData();
      }
    } else {
      fetchData();
    }
  }, []);

  const renderStepContent = () => {
    if (!data) return <div style={styles.noData}>Нет данных для отображения.</div>;

    // Отображаем все матрицы на главном экране "Задание"
    if (activeStep === 0) {
      return (
        <div style={styles.matricesContainer}>
          {data.variant.variance.matrices.map((matrix, matrixIndex) => (
            <MatrixDisplay key={matrixIndex} matrix={matrix} matrixIndex={matrixIndex} styles={styles} />
          ))}
        </div>
      );
    }

    // Если активный шаг соответствует индексу матрицы, отображаем соответствующую матрицу
    if (activeStep > 0 && activeStep <= data.variant.variance.matrices.length) {
      const matrixIndex = activeStep - 1;
      return (
        <MatrixDisplay
          matrix={data.variant.variance.matrices[matrixIndex]}
          matrixIndex={matrixIndex}
          styles={styles}
        />
      );
    }

    // Для остальных шагов (например, "Результат")
    if (activeStep === steps.length - 1) {
      return <div>Здесь будут отображаться результаты выполнения.</div>;
    }

    return <div>Содержимое для шага {steps[activeStep]}</div>;
  };

  if (loading) {
    return <div className="loading">Загрузка...</div>;
  }

  if (error) {
    return <div className="error">Ошибка: {error}</div>;
  }

  return (
    <div style={styles.container}>
      <h1 style={styles.title}>Generated Lab1A Variance</h1>
      {data ? (
        <div>
          <h2 style={styles.subtitle}>Variant Number: {data.variant.number}</h2>
          <TaskBar activeStep={activeStep} steps={steps} onStepChange={setActiveStep} />
          {renderStepContent()}
        </div>
      ) : (
        <div style={styles.noData}>Нет данных для отображения.</div>
      )}
    </div>
  );
};
