import React, { useState } from "react";
import MatrixDisplay from "./MatrixDisplay";

type MatrixTaskProps = {
  matrix: number[][];
};

const MatrixTask: React.FC<MatrixTaskProps> = ({ matrix }) => {
  const [lambdaMax, setLambdaMax] = useState<number | null>(null);
  const [isValue, setIsValue] = useState<number | null>(null); // ИС
  const [osValue, setOsValue] = useState<number | null>(null); // ОС

  // Вычисляем дополнительные столбцы: w, M * w, λ * w
  const columnNames = ["x", "w", "M * w", "λ * w"];
  const generatedMatrix = matrix.map((row, rowIndex) => {
    const w = row.reduce((acc, val) => acc + val, 0); // Пример: сумма строки
    const mTimesW = row.map((val) => val * w); // Умножение строки на w
    const lambdaTimesW = mTimesW.map((val) => val * (lambdaMax || 1)); // Умножение строки на λMax (по умолчанию 1)
    return [rowIndex + 1, w, mTimesW[0], lambdaTimesW[0]]; // Создание строки с данными
  });

  return (
    <div>
      <MatrixDisplay matrix={matrix} title="Основная матрица" />
      <h3 style={styles.subTitle}>Дополнительная матрица</h3>
      <table style={styles.table}>
        <thead>
          <tr>
            {columnNames.map((colName, index) => (
              <th key={index} style={styles.cell}>
                {colName}
              </th>
            ))}
          </tr>
        </thead>
        <tbody>
          {generatedMatrix.map((row, rowIndex) => (
            <tr key={rowIndex}>
              {row.map((cell, cellIndex) => (
                <td key={cellIndex} style={styles.cell}>
                  {cell}
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>

      <h3 style={styles.subTitle}>Введите данные</h3>
      <div style={styles.inputContainer}>
        <label>
          λMax:{" "}
          <input
            type="number"
            value={lambdaMax || ""}
            onChange={(e) => setLambdaMax(parseFloat(e.target.value) || null)}
            style={styles.input}
          />
        </label>
        <label>
          ИС:{" "}
          <input
            type="number"
            value={isValue || ""}
            onChange={(e) => setIsValue(parseFloat(e.target.value) || null)}
            style={styles.input}
          />
        </label>
        <label>
          ОС:{" "}
          <input
            type="number"
            value={osValue || ""}
            onChange={(e) => setOsValue(parseFloat(e.target.value) || null)}
            style={styles.input}
          />
        </label>
      </div>
    </div>
  );
};

const styles: { [key: string]: React.CSSProperties } = {
  subTitle: {
    textAlign: "center",
    margin: "20px 0 10px",
    fontSize: "18px",
  },
  table: {
    width: "100%",
    borderCollapse: "collapse",
    margin: "10px 0",
  },
  cell: {
    border: "1px solid #ccc",
    padding: "10px",
    textAlign: "center",
  },
  inputContainer: {
    display: "flex",
    justifyContent: "space-between",
    gap: "10px",
    margin: "20px 0",
  },
  input: {
    padding: "5px",
    fontSize: "14px",
    border: "1px solid #ccc",
    borderRadius: "4px",
  },
};

export default MatrixTask;
