import React, { useState } from "react";

type MatrixDisplayProps = {
  matrix: number[][];
  title?: string;
};

const MatrixDisplay: React.FC<MatrixDisplayProps> = ({ matrix, title }) => {
  const [lambdaMax, setLambdaMax] = useState<number>(0);
  const [IS, setIS] = useState<number>(0);
  const [OS, setOS] = useState<number>(0);

  // Генерация второй матрицы 4xN
  const matrix4 = [
    ['x', 'w', 'M * w', 'lambda * w'],
    ...matrix.map((row) => row.map((cell) => cell)), // Можно заменить на свои вычисления для M*w и lambda*w
  ];

  return (
    <div style={styles.container}>
      {title && <h3 style={styles.title}>{title}</h3>}

      {/* Первая матрица */}
      <Matrix table={matrix} />

      {/* Вторая матрица 4xN */}
      <Matrix table={matrix4} />

      {/* Поля для ввода */}
      <div style={styles.inputContainer}>
        <label style={styles.inputLabel}>lambdaMax:</label>
        <input
          type="number"
          value={lambdaMax}
          onChange={(e) => setLambdaMax(Number(e.target.value))}
          style={styles.input}
        />
        <label style={styles.inputLabel}>ИС:</label>
        <input
          type="number"
          value={IS}
          onChange={(e) => setIS(Number(e.target.value))}
          style={styles.input}
        />
        <label style={styles.inputLabel}>ОС:</label>
        <input
          type="number"
          value={OS}
          onChange={(e) => setOS(Number(e.target.value))}
          style={styles.input}
        />
      </div>
    </div>
  );
};

// Компонент для отображения таблицы
const Matrix: React.FC<{ table: any[] }> = ({ table }) => (
  <table style={styles.table}>
    <tbody>
      {table.map((row, rowIndex) => (
        <tr key={rowIndex}>
          {row.map((cell, cellIndex) => (
            <td key={cellIndex} style={styles.cell}>
              {cell}
            </td>
          ))}
        </tr>
      ))}
    </tbody>
  </table>
);

const styles: { [key: string]: React.CSSProperties } = {
  container: {
    background: "#f9f9f9",
    borderRadius: "8px",
    boxShadow: "0 4px 6px rgba(0, 0, 0, 0.1)",
    padding: "15px",
    textAlign: "center",
    margin: "10px 0",
  },
  title: {
    marginBottom: "10px",
    fontSize: "18px",
    fontWeight: "bold",
  },
  table: {
    width: "100%",
    borderCollapse: "collapse",
    marginBottom: "20px",
  },
  cell: {
    border: "1px solid #ccc",
    padding: "10px",
    textAlign: "center",
    minWidth: "50px",
    maxWidth: "50px",
    height: "50px",
  },
  inputContainer: {
    display: "flex",
    flexDirection: "column",
    gap: "10px",
    marginTop: "20px",
  },
  inputLabel: {
    fontSize: "14px",
    fontWeight: "bold",
  },
  input: {
    padding: "8px",
    border: "1px solid #ccc",
    borderRadius: "4px",
  },
};

export default MatrixDisplay;
