import React from "react";

type MatrixDisplayProps = {
  matrix: number[][];
  title?: string;
  matrixIndex?: number; // Индекс матрицы для изменения левого верхнего угла
};

const headersFirstMatrix = ["Д", "Р", "С", "Л"];  // Заголовки для первой матрицы
const headersSubsequentMatrices = ["А", "Б", "В"];  // Заголовки для последующих матриц
const rowHeaders = ["Д", "Р", "С", "Л"];  // Строки, одинаковые для всех матриц

const MatrixDisplay: React.FC<MatrixDisplayProps> = ({ matrix, title, matrixIndex = 0 }) => {
  // Определяем, что будет в левом верхнем углу для последующих матриц
  const leftTopCell = rowHeaders[matrixIndex] || "";

  // Заголовки для текущей матрицы
  const columnHeaders = matrixIndex === 0 ? headersFirstMatrix : headersSubsequentMatrices;

  return (
    <div style={styles.container}>
      {title && <h3 style={styles.title}>{title}</h3>}
      <table style={styles.table}>
        <thead>
          <tr>
            <td style={styles.cell}></td> {/* Пустая ячейка в левом верхнем углу */}
            {columnHeaders.map((header, index) => (
              <td key={index} style={styles.cell}>
                {header}
              </td>
            ))}
          </tr>
        </thead>
        <tbody>
          {matrix.map((row, rowIndex) => (
            <tr key={rowIndex}>
              <td style={styles.cell}>{rowHeaders[rowIndex]}</td> {/* Заголовок строки */}
              {row.map((cell, cellIndex) => (
                <td key={cellIndex} style={styles.cell}>
                  {cell}
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

const styles: { [key: string]: React.CSSProperties } = {
  container: {
    background: "#f9f9f9",
    borderRadius: "8px",
    boxShadow: "0 4px 6px rgba(0, 0, 0, 0.1)",
    padding: "15px",
    textAlign: "center",
    margin: "10px 0",
  },
  title: {
    marginBottom: "10px",
    fontSize: "18px",
    fontWeight: "bold",
  },
  table: {
    width: "100%",
    borderCollapse: "collapse",
  },
  cell: {
    border: "1px solid #ccc",
    padding: "10px",
    textAlign: "center",
    minWidth: "50px",
    maxWidth: "50px",
    height: "50px",
  },
};

export default MatrixDisplay;
