import React from "react";

type MatrixDisplayProps = {
  matrix: number[][];
  title?: string;
};

const MatrixDisplay: React.FC<MatrixDisplayProps> = ({ matrix, title }) => {
  return (
    <div style={styles.container}>
      {title && <h3 style={styles.title}>{title}</h3>}
      <table style={styles.table}>
        <tbody>
          {matrix.map((row, rowIndex) => (
            <tr key={rowIndex}>
              {row.map((cell, cellIndex) => (
                <td key={cellIndex} style={styles.cell}>
                  {cell}
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

const styles: { [key: string]: React.CSSProperties } = {
  container: {
    background: "#f9f9f9",
    borderRadius: "8px",
    boxShadow: "0 4px 6px rgba(0, 0, 0, 0.1)",
    padding: "15px",
    textAlign: "center",
    marginBottom: "20px", // Отступ между контейнерами
  },
  title: {
    marginBottom: "10px",
    fontSize: "18px",
    fontWeight: "bold",
  },
  table: {
    width: "100%",
    borderCollapse: "collapse",
    margin: "20px 0", // Добавляем отступ между таблицами
    display: "block", // Таблица будет блоком, что гарантирует вывод с новой строки
  },
  cell: {
    border: "1px solid #ccc",
    padding: "10px",
    textAlign: "center",
    minWidth: "50px",
    maxWidth: "50px",
    height: "50px",
  },
};

export default MatrixDisplay;
