import React, { useEffect, useState } from 'react';

interface Result {
  step: number;
  percentage: number;
  maxMark: number;
}

const LabResultsTable: React.FC = () => {
  const [results, setResults] = useState<Result[]>([]); // Явно указываем тип
  const [totalPercentage, setTotalPercentage] = useState(0);
  const [totalMaxMark, setTotalMaxMark] = useState(0);

  useEffect(() => {
    let totalPercentage = 0;
    let totalMaxMark = 0;
    const resultsArr: Result[] = []; // Указываем тип для временного массива

    for (let step = 1; step <= 6; step++) {
      const key = `lab1a_${step}_result`;
      const storedResult = localStorage.getItem(key);

      if (storedResult) {
        try {
          const parsedResult = JSON.parse(storedResult);
          const { percentage, max_mark } = parsedResult;

          if (percentage !== undefined && max_mark !== undefined) {
            resultsArr.push({
              step,
              percentage,
              maxMark: max_mark
            });

            totalPercentage += percentage;
            totalMaxMark += max_mark;
          }
        } catch (error) {
          console.error(`Ошибка при парсинге для ${key}:`, error);
        }
      }
    }

    setResults(resultsArr);
    setTotalPercentage(totalPercentage);
    setTotalMaxMark(totalMaxMark);
  }, []);

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial, sans-serif' }}>
      <h2>Результаты Лабораторной Работы</h2>
      <table style={{ width: '100%', borderCollapse: 'collapse' }}>
        <thead>
          <tr>
            <th style={tableHeaderStyle}>Шаг</th>
            <th style={tableHeaderStyle}>Процент / Макс. балл</th>
          </tr>
        </thead>
        <tbody>
          {results.map(({ step, percentage, maxMark }) => (
            <tr key={step}>
              <td style={tableCellStyle}>Шаг {step}</td>
              <td style={tableCellStyle}>{percentage}/{maxMark}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <div style={totalStyle}>
        <h3>Итого:</h3>
        <p>{totalPercentage}/{totalMaxMark} ({((totalPercentage / totalMaxMark) * 100).toFixed(2)}%)</p>
      </div>
    </div>
  );
};

// Стиль для заголовков таблицы
const tableHeaderStyle = {
  textAlign: 'left',
  padding: '10px',
  backgroundColor: '#f4f4f4',
  borderBottom: '1px solid #ccc'
};

// Стиль для ячеек таблицы
const tableCellStyle = {
  padding: '10px',
  borderBottom: '1px solid #ccc'
};

// Стиль для итоговой информации
const totalStyle = {
  marginTop: '20px',
  fontSize: '16px',
  fontWeight: 'bold'
};

export default LabResultsTable;
