import React, { useEffect, useState } from "react";
import axios from "axios";

type Lab1AVariance = {
  matrices: number[][][];
};

type GeneratedLab1AVariance = {
  number: number;
  variance: Lab1AVariance;
};

type Lab1AVariant = {
	user_id: number;
	variant: GeneratedLab1AVariance;
}

const Lab1aVariance: React.FC = () => {
  const [data, setData] = useState<Lab1AVariant[]>([]);
  const [loading, setLoading] = useState<boolean>(false);
  const [error, setError] = useState<string | null>(null);

  const fetchData = async () => {
    setLoading(true);
    setError(null);

    try {
      const token =
        "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJleHAiOjE3MzQwNzI1ODIsImlhdCI6MTczNDAyOTM4MiwidXNlcl9pZCI6NCwidXNlcl9wb3N0Ijoic3R1ZGVudCJ9.HVKOr05Ejm9qEdWu1S-AJQBmrgUTu1pjADdHV8vFGjI";
      const labToken = "ajkdfnshdabvpdhifvndsfjhlnfsjdlfkjsdfsdfsdf";

      const response = await axios.get<Lab1AVariant>("http://localhost:8001/lab1a/variant", {
        headers: {
          Authorization: `Bearer ${token}`,
          "lab-token": labToken,
        },
        params: {
          laboratory_id: 1,
        },
      });

      setData(response.data);
    } catch (err: any) {
      setError(err.message || "Ошибка при выполнении запроса");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

	return (
    <div style={{ padding: "20px" }}>
      <h1>Generated Lab1A Variance</h1>

      <div>
            <div>
              {data.variant.variance.matrices.map((matrix, idx) => (
                <div key={idx} style={{ marginBottom: "15px" }}>
                  <p>Matrix {idx + 1}:</p>
                  <table border={1} style={{ borderCollapse: "collapse" }}>
                    <tbody>
                      {matrix.map((row, rowIndex) => (
                        <tr key={rowIndex}>
                          {row.map((value, colIndex) => (
                            <td key={colIndex} style={{ padding: "5px" }}>
                              {value}
                            </td>
                          ))}
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              ))}
            </div>
          </div>
      </div>
  );
};

export default Lab1aVariance;
