import React, { useState } from "react";

type IsRightInt = {
  val: number;
  is_right: boolean;
};

type AnswerLab1ACommonMatrixIsRight = {
  x: IsRightInt[];
  w: IsRightInt[];
  mw: IsRightInt[];
  lambda_w: IsRightInt[];
  lambda_max: IsRightInt;
  is: IsRightInt;
  os: IsRightInt;
};

const MatrixTask: React.FC<MatrixTaskProps> = ({ matrix, step, title }) => {
  const [response, setResponse] = useState<AnswerLab1ACommonMatrixIsRight | null>(null);
  const [percentage, setPercentage] = useState<number>(0);
  const [maxMark, setMaxMark] = useState<number>(0);
  const [isLoading, setIsLoading] = useState<boolean>(false);

  // Функция для обработки и отображения результата
  const handleServerResponse = (data: any) => {
    setPercentage(data.percentage); // Пример использования значения из ответа
    setMaxMark(data.max_mark); // Максимальный балл из ответа

    // Обрабатываем результат
    setResponse(data.result);
  };

  // Отправка запроса на сервер
  const handleSubmit = async () => {
    setIsLoading(true);

    // Тут код для отправки запроса
    try {
      const response = await fetch(`http://localhost:8001/lab1a/variant/1`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          // Данные для отправки на сервер
        }),
      });

      const data = await response.json();
      if (response.ok) {
        handleServerResponse(data); // Обработка ответа
      } else {
        alert("Ошибка на сервере");
      }
    } catch (error) {
      alert("Ошибка при отправке запроса");
    } finally {
      setIsLoading(false);
    }
  };

  const renderResult = (resultArray: IsRightInt[], label: string) => {
    return (
      <div>
        <h4>{label}:</h4>
        {resultArray.map((item, index) => (
          <div
            key={index}
            style={{
              color: item.is_right ? "black" : "red",
            }}
          >
            {item.val}
          </div>
        ))}
      </div>
    );
  };

  return (
    <div style={styles.container}>
      {title && <h3 style={styles.title}>{title}</h3>}

      <button onClick={handleSubmit} style={styles.button}>
        Отправить
      </button>

      {isLoading && <p>Загрузка...</p>}

      {response && (
        <div>
          {/* Отображаем процент и максимальный балл */}
          <h4>
            Получено: {percentage}/{maxMark}
          </h4>

          {/* Отображаем данные результата */}
          <div style={styles.resultContainer}>
            {renderResult(response.x, "x")}
            {renderResult(response.w, "w")}
            {renderResult(response.mw, "M * w")}
            {renderResult(response.lambda_w, "λ * w")}

            {/* λmax */}
            <div>
              <h4>λmax:</h4>
              <div style={{ color: response.lambda_max.is_right ? "black" : "red" }}>
                {response.lambda_max.val}
              </div>
            </div>

            {/* ИС */}
            <div>
              <h4>ИС:</h4>
              <div style={{ color: response.is.is_right ? "black" : "red" }}>
                {response.is.val}
              </div>
            </div>

            {/* ОС */}
            <div>
              <h4>ОС:</h4>
              <div style={{ color: response.os.is_right ? "black" : "red" }}>
                {response.os.val}
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

const styles: { [key: string]: React.CSSProperties } = {
  container: {
    background: "#f9f9f9",
    borderRadius: "8px",
    padding: "30px",
    textAlign: "center",
  },
  title: {
    fontSize: "24px",
    fontWeight: "bold",
    color: "#333",
  },
  button: {
    backgroundColor: "#4CAF50",
    color: "white",
    padding: "10px 20px",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
    fontSize: "16px",
  },
  resultContainer: {
    marginTop: "20px",
    textAlign: "left",
  },
};

export default MatrixTask;
