import React, { useState, useEffect } from "react";
import axios from "axios";

type isRight = {
	val: number;
	is_right: boolean;
}

type reqRes = {
	index: isRight;
	max_mark: number;
	percentage: number;
	result: isRight[];
}

type reqInput = {
	set: number[];
	chosen_index: number;
}

const ElementSelector = () => {
  const [elements, setElements] = useState(['', '', '']);
  const [selectedElement, setSelectedElement] = useState(null);
  const [chosenIndex, setChosenIndex] = useState(0);
	const [reqResp, setReqResp] = useState<reqRes>({})
  const [isSubmitted, setIsSubmitted] = useState(false); 
	const [reqIn, setReqIn] = useState<reqInput>({})

  const handleChange = (index, value) => {
    if (/^\d*\.?\d*$/.test(value)) {
      const newElements = [...elements];
      newElements[index] = value;
      setElements(newElements);
    }
  };

	useEffect(() => {
			const savedData = localStorage.getItem(`lab1a_6_data`);
			const resData = localStorage.getItem(`lab1a_6_result`);
			if (savedData && resData) {
				let data = JSON.parse(savedData);
				setChosenIndex(data.chosen_index);
				setElements(data.set)

				let resDataL =JSON.parse(resData);
				setReqResp(resDataL)
				setIsSubmitted(true); 
			} else {
				setIsSubmitted(false); 
			}
		}, []);

  const handleSelect = (index) => {
    setSelectedElement(elements[index]);
    setChosenIndex(index);
  };

  const roundNumbers = (inputElements) => {
    return inputElements.map((el) => {
      const num = parseFloat(el);
      if (!isNaN(num)) {
        return num.toFixed(2); 
      }
      return el; 
    });
  };

  const handleSubmit = async () => {
    const roundedElements = roundNumbers(elements);
    setIsSubmitted(true);

		setReqIn(
			{
				set:  roundedElements,
				chosenIndex: chosenIndex,
			}
		)


		let token = localStorage.getItem("token");
    let labToken = localStorage.getItem("lab-token");

    try {
			const response = await axios.post<reqRes>(
				`http://localhost:8001/lab1a/variant/result`,
				reqIn, 
				{
					headers: {
						Authorization: `Bearer ${token}`,
						'lab-token': labToken,
						'Content-Type': 'application/json', 
					}
				}
			);

			setReqResp(response.data);

      if (response) {
        localStorage.setItem(`lab1a_6_data`, JSON.stringify(requestData));

        localStorage.setItem(`lab1a_6_result`, JSON.stringify(response.data));

        setIsSubmitted(true); 
      } else {
        alert("Произошла ошибка при отправке данных.");
      }
    } catch (error) {
      alert("Произошла ошибка при отправке запроса.");
    }
  };

  return (
    <div style={styles.container}>
      <h2>Введите 3 элемента</h2>
      <div style={styles.inputContainer}>
        {['A', 'B', 'C'].map((label, index) => (
          <div key={index} style={styles.inputWrapper}>
            <label style={styles.label}>{label}</label>
            <input
              type="text"
              value={elements[index]}
              onChange={(e) => handleChange(index, e.target.value)}
              placeholder={`Элемент ${label}`}
              style={styles.input}
            />
          </div>
        ))}
      </div>

      {elements.every((el) => el) && !isSubmitted && (
        <div style={styles.selectContainer}>
          <h3>Выберите элемент:</h3>
          <div style={styles.buttonGroup}>
            {['A', 'B', 'C'].map((label, index) => (
              <button
                key={index}
                onClick={() => handleSelect(index)}
                style={{
                  ...styles.button,
                  backgroundColor: chosenIndex === index ? '#4CAF50' : '#2196F3',
                  border: chosenIndex === index ? '2px solid #fff' : 'none',
                  color: 'white',
                }}
              >
                {label}
              </button>
            ))}
          </div>
        </div>
      )}

      {selectedElement && (
        <div style={styles.selectedElementContainer}>
          <h3>Вы выбрали: {selectedElement}</h3>
        </div>
      )}

      {selectedElement !== null && !isSubmitted && (
        <button onClick={handleSubmit} style={styles.submitButton}>
          Отправить
        </button>
      )}
    </div>
  );
};

const styles = {
  container: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    padding: '20px',
    fontFamily: 'Arial, sans-serif',
  },
  inputContainer: {
    display: 'flex',
    gap: '10px',
    marginBottom: '20px',
  },
  inputWrapper: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  },
  label: {
    fontSize: '18px',
    marginBottom: '5px',
  },
  input: {
    padding: '10px',
    fontSize: '16px',
    borderRadius: '5px',
    border: '1px solid #ccc',
    width: '150px',
  },
  selectContainer: {
    marginTop: '20px',
  },
  buttonGroup: {
    display: 'flex',
    gap: '10px',
  },
  button: {
    padding: '10px 20px',
    fontSize: '16px',
    borderRadius: '5px',
    border: 'none',
    cursor: 'pointer',
    transition: 'background-color 0.3s, border 0.3s',
  },
  selectedElementContainer: {
    marginTop: '20px',
    fontSize: '18px',
    fontWeight: 'bold',
  },
  submitButton: {
    marginTop: '20px',
    padding: '10px 20px',
    backgroundColor: '#4CAF50',
    color: 'white',
    border: 'none',
    borderRadius: '5px',
    fontSize: '16px',
    cursor: 'pointer',
    transition: 'background-color 0.3s',
  },
};

export default ElementSelector;
