import React, { useState } from 'react';

const ElementSelector = () => {
  const [elements, setElements] = useState(['', '', '']);
  const [selectedElement, setSelectedElement] = useState(null);
  const [chosenIndex, setChosenIndex] = useState(null);

  // Обработчик изменения значения в поле ввода
  const handleChange = (index, value) => {
    const newElements = [...elements];
    newElements[index] = value;
    setElements(newElements);
  };

  // Обработчик выбора элемента
  const handleSelect = (index) => {
    setSelectedElement(elements[index]);
    setChosenIndex(index);
  };

  // Функция для округления чисел в ячейках до 2 знаков после запятой
  const roundNumbers = () => {
    const roundedElements = elements.map((el) => {
      // Проверяем, является ли значение числом
      const num = parseFloat(el);
      if (!isNaN(num)) {
        return num.toFixed(2); // Округляем до 2 знаков после запятой
      }
      return el; // Если это не число, оставляем как есть
    });
    setElements(roundedElements);  // Обновляем состояние с округленными значениями
  };

  // Отправка данных в консоль
  const handleSubmit = () => {
    roundNumbers(); // Округляем значения перед выводом в консоль
    console.log('Set:', elements);
    console.log('Chosen Index:', chosenIndex);
  };

  return (
    <div style={styles.container}>
      <h2>Введите 3 элемента</h2>
      <div style={styles.inputContainer}>
        {['A', 'B', 'C'].map((label, index) => (
          <div key={index} style={styles.inputWrapper}>
            <label style={styles.label}>{label}</label>
            <input
              type="text"
              value={elements[index]}
              onChange={(e) => handleChange(index, e.target.value)}
              placeholder={`Элемент ${label}`}
              style={styles.input}
            />
          </div>
        ))}
      </div>

      {elements.every((el) => el) && (
        <div style={styles.selectContainer}>
          <h3>Выберите элемент:</h3>
          <div style={styles.buttonGroup}>
            {['A', 'B', 'C'].map((label, index) => (
              <button
                key={index}
                onClick={() => handleSelect(index)}
                style={{
                  ...styles.button,
                  backgroundColor: chosenIndex === index ? '#4CAF50' : '#2196F3',
                  border: chosenIndex === index ? '2px solid #fff' : 'none',
                  color: 'white',
                }}
              >
                {label}
              </button>
            ))}
          </div>
        </div>
      )}

      {selectedElement && (
        <div style={styles.selectedElementContainer}>
          <h3>Вы выбрали: {selectedElement}</h3>
        </div>
      )}

      {selectedElement !== null && (
        <button onClick={handleSubmit} style={styles.submitButton}>
          Отправить
        </button>
      )}
    </div>
  );
};

const styles = {
  container: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    padding: '20px',
    fontFamily: 'Arial, sans-serif',
  },
  inputContainer: {
    display: 'flex',
    gap: '10px',
    marginBottom: '20px',
  },
  inputWrapper: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  },
  label: {
    fontSize: '18px',
    marginBottom: '5px',
  },
  input: {
    padding: '10px',
    fontSize: '16px',
    borderRadius: '5px',
    border: '1px solid #ccc',
    width: '150px',
  },
  selectContainer: {
    marginTop: '20px',
  },
  buttonGroup: {
    display: 'flex',
    gap: '10px',
  },
  button: {
    padding: '10px 20px',
    fontSize: '16px',
    borderRadius: '5px',
    border: 'none',
    cursor: 'pointer',
    transition: 'background-color 0.3s, border 0.3s',
  },
  selectedElementContainer: {
    marginTop: '20px',
    fontSize: '18px',
    fontWeight: 'bold',
  },
  submitButton: {
    marginTop: '20px',
    padding: '10px 20px',
    backgroundColor: '#4CAF50',
    color: 'white',
    border: 'none',
    borderRadius: '5px',
    fontSize: '16px',
    cursor: 'pointer',
    transition: 'background-color 0.3s',
  },
};

export default ElementSelector;
