import React, { useState, useEffect } from "react";
import Lab1AResults1Display from "./Lab1AResults1Display.tsx";

type MatrixTaskProps = {
  matrix: number[][];
  step: number;
  title?: string;
};

const Lab1AMatrixTask1Step: React.FC<MatrixTaskProps> = ({ matrix, step, title }) => {
  const [lambdaMax, setLambdaMax] = useState<number | string>("");
  const [IS, setIS] = useState<number | string>("");
  const [OS, setOS] = useState<number | string>("");
  const [matrix4, setMatrix4] = useState(
    matrix.map(() => Array(matrix[0].length).fill(''))
  );
  const [isSubmitted, setIsSubmitted] = useState(false); // Состояние для отслеживания отправки
  const [responseData, setResponseData] = useState<any>(null); // Состояние для хранения данных результата

  // Проверка наличия данных в localStorage при загрузке компонента
  useEffect(() => {
    const savedData = localStorage.getItem('lab1a_1_data');
    if (savedData) {
      setIsSubmitted(true); // Если данные есть, считаем, что они уже отправлены
    }

    // Получаем результат из localStorage
    const savedResult = localStorage.getItem('lab1a_1_result');
    if (savedResult) {
      setResponseData(JSON.parse(savedResult)); // Сохраняем данные ответа
    }
  }, []);

  const handleMatrixChange = (rowIndex: number, colIndex: number, value: string) => {
    if (isSubmitted) return; // Если данные отправлены, не разрешаем изменения
    const newMatrix = [...matrix4];
    newMatrix[rowIndex][colIndex] = value;
    setMatrix4(newMatrix);
  };

  const roundToTwoDecimals = (value: string) => {
    if (value === "") {
      return "0.00";
    }
    return parseFloat(value).toFixed(2);
  };

  const handleSubmit = async () => {
    const x = matrix4.map(row => roundToTwoDecimals(row[0] || ""));
    const w = matrix4.map(row => roundToTwoDecimals(row[1] || ""));
    const mw = matrix4.map(row => roundToTwoDecimals(row[2] || ""));
    const lambdaW = matrix4.map(row => roundToTwoDecimals(row[3] || ""));

    const requestData = {
      x: x.map((val) => parseFloat(val)),
      w: w.map((val) => parseFloat(val)),
      mw: mw.map((val) => parseFloat(val)),
      lambda_w: lambdaW.map((val) => parseFloat(val)),
      lambda_max: parseFloat(roundToTwoDecimals(lambdaMax)),
      is: parseFloat(roundToTwoDecimals(IS)),
      os: parseFloat(roundToTwoDecimals(OS)),
    };

    const bearerToken = localStorage.getItem("jwt-token");
    const labToken = localStorage.getItem("lab-token");

    try {
      const response = await fetch(`http://localhost:8001/lab1a/variant/1`, {
        method: "POST",
        headers: {
          "Authorization": `Bearer ${bearerToken}`,
          "lab-token": labToken,
          "Content-Type": "application/json",
        },
        body: JSON.stringify(requestData),
      });

      if (response.ok) {
        const data = await response.json();
        console.log("Ответ от сервера:", data);
        alert("Данные успешно отправлены!");

        // Сохраняем данные в localStorage
        localStorage.setItem('lab1a_1_data', JSON.stringify(requestData));

        // Сохраняем результат в localStorage с ключом lab1a_1_result
        localStorage.setItem('lab1a_1_result', JSON.stringify(data));

        setIsSubmitted(true); // После отправки ставим состояние isSubmitted в true
      } else {
        const error = await response.json();
        console.error("Ошибка от сервера:", error);
        alert("Произошла ошибка при отправке данных.");
      }
    } catch (error) {
      console.error("Ошибка при отправке запроса:", error);
      alert("Произошла ошибка при отправке запроса.");
    }
  };

  return (
    <div style={styles.container}>
      {title && <h3 style={styles.title}>{title}</h3>}

      <div style={styles.matrixContainer}>
        <h4>Исходная матрица</h4>
        <table style={styles.table}>
          <tbody>
            {matrix.map((row, rowIndex) => (
              <tr key={rowIndex}>
                {row.map((cell, cellIndex) => (
                  <td key={cellIndex} style={styles.cell}>
                    {cell}
                  </td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      <div style={styles.matrixContainer}>
        <h4>Матрица с вычислениями</h4>
        <table style={styles.table}>
          <thead>
            <tr>
              {['x', 'w', 'M * w', 'λ * w'].map((header, index) => (
                <th key={index} style={styles.headerCell}>{header}</th>
              ))}
            </tr>
          </thead>
          <tbody>
            {matrix4.map((row, rowIndex) => (
              <tr key={rowIndex}>
                {row.map((cell, cellIndex) => {
                  const isWrong = responseData && responseData.result?.x?.[rowIndex]?.is_right === false;
                  return (
                    <td key={cellIndex} style={styles.cell}>
                      <input
                        type="number"
                        step="0.01"
                        value={cell || ""}
                        onChange={(e) =>
                          handleMatrixChange(rowIndex, cellIndex, e.target.value)
                        }
                        disabled={isSubmitted} // Отключаем редактирование, если отправлено
                        style={isWrong ? { ...styles.inputCell, borderColor: 'red' } : styles.inputCell}
                      />
                    </td>
                  );
                })}
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      <div style={styles.inputContainer}>
        <div style={styles.inputGroupRow}>
          <div style={styles.inputGroup}>
            <label style={styles.inputLabel}>λmax:</label>
            <input
              type="number"
              value={lambdaMax || ""}
              onChange={(e) => setLambdaMax(e.target.value)}
              disabled={isSubmitted} // Отключаем редактирование, если отправлено
              style={styles.input}
            />
          </div>

          <div style={styles.inputGroup}>
            <label style={styles.inputLabel}>ИС:</label>
            <input
              type="number"
              value={IS || ""}
              onChange={(e) => setIS(e.target.value)}
              disabled={isSubmitted} // Отключаем редактирование, если отправлено
              style={styles.input}
            />
          </div>

          <div style={styles.inputGroup}>
            <label style={styles.inputLabel}>ОС:</label>
            <input
              type="number"
              value={OS || ""}
              onChange={(e) => setOS(e.target.value)}
              disabled={isSubmitted} // Отключаем редактирование, если отправлено
              style={styles.input}
            />
          </div>
        </div>
      </div>

      {/* Кнопка отправки, скрываем её, если отправка завершена */}
      {!isSubmitted && <button onClick={handleSubmit} style={styles.button}>Отправить</button>}

      {isSubmitted && responseData && (
        <div style={styles.matrixContainer}>
          <h4>Результаты после отправки</h4>
          <table style={styles.table}>
            <thead>
              <tr>
                {['x', 'w', 'M * w', 'λ * w'].map((header, index) => (
                  <th key={index} style={styles.headerCell}>{header}</th>
                ))}
              </tr>
            </thead>
            <tbody>
              {responseData.result?.x.map((row, rowIndex) => (
                <tr key={rowIndex}>
                  {row.val.map((cell: any, cellIndex: number) => {
                    const isWrong = row.is_right === false;
                    return (
                      <td key={cellIndex} style={styles.cell}>
                        <span style={isWrong ? { color: 'red' } : {}}>{cell}</span>
                      </td>
                    );
                  })}
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

