import React from "react";

type MatrixDisplayProps = {
  matrix: number[][];
  title?: string;
  index: number; // Добавим индекс для отличия таблиц
};

const MatrixDisplay: React.FC<MatrixDisplayProps> = ({ matrix, index }) => {
  // Заголовки для таблиц
  const firstTableHeaders = ['Д', 'Р', 'С', 'Л'];
  const secondTableHeaders = ['Р', 'С', 'Л', 'Д'];
  const thirdTableHeaders = ['С', 'Л', 'Д', 'Р'];
  const fourthTableHeaders = ['Л', 'Д', 'Р', 'С'];

  const getTableHeaders = () => {
    switch (index) {
      case 1:
        return firstTableHeaders;
      case 2:
        return secondTableHeaders;
      case 3:
        return thirdTableHeaders;
      case 4:
        return fourthTableHeaders;
      default:
        return [];
    }
  };

  const getRowHeaders = () => {
    switch (index) {
      case 1:
        return firstTableHeaders;
      case 2:
        return secondTableHeaders;
      case 3:
        return thirdTableHeaders;
      case 4:
        return fourthTableHeaders;
      default:
        return [];
    }
  };

  // Функция для рендеринга таблицы
  const renderTable = () => {
    const headers = getTableHeaders();
    const rowHeaders = getRowHeaders();

    return (
      <table style={styles.table}>
        <thead>
          <tr>
            <th style={styles.verticalHeader}></th>
            {headers.map((header, index) => (
              <th key={index} style={styles.cell}>
                {header}
              </th>
            ))}
          </tr>
        </thead>
        <tbody>
          {matrix.map((row, rowIndex) => (
            <tr key={rowIndex}>
              <td style={styles.verticalHeader}>
                {rowHeaders[rowIndex]}
              </td>
              {row.map((cell, cellIndex) => (
                <td key={cellIndex} style={styles.cell}>
                  {cell}
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    );
  };

  return (
    <div style={styles.container}>
      {renderTable()}
    </div>
  );
};

const styles: { [key: string]: React.CSSProperties } = {
  container: {
    background: "#f9f9f9",
    borderRadius: "8px",
    boxShadow: "0 4px 6px rgba(0, 0, 0, 0.1)",
    padding: "15px",
    textAlign: "center",
    margin: "10px 0",
  },
  title: {
    marginBottom: "10px",
    fontSize: "18px",
    fontWeight: "bold",
  },
  table: {
    width: "100%",
    borderCollapse: "collapse",
  },
  cell: {
    border: "1px solid #ccc",
    padding: "10px",
    textAlign: "center",
    minWidth: "50px",
    maxWidth: "50px",
    height: "50px",
  },
  verticalHeader: {
    border: "1px solid #ccc",
    padding: "10px",
    textAlign: "center",
    width: "50px",
    height: "50px",
    writingMode: "vertical-rl", // Вертикальное написание
    transform: "rotate(180deg)", // Поворот текста для нормального отображения
    fontWeight: "bold", // Жирный шрифт для вертикальных заголовков
  },
};

export default MatrixDisplay;
