import React, { useEffect, useState } from "react";
import axios from "axios";

type Lab1AVariance = {
  matrices: number[][][];
};

type GeneratedLab1AVariance = {
  number: number;
  variance: Lab1AVariance;
};

const Lab1aVariance: React.FC = () => {
  const [data, setData] = useState<GeneratedLab1AVariance[]>([]);
  const [loading, setLoading] = useState<boolean>(false);
  const [error, setError] = useState<string | null>(null);

  const fetchData = async () => {
		setLoading(true);
		setError(null);

		try {
			const token =
				"eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJleHAiOjE3MzM3MTQ3NzQsImlhdCI6MTczMzY3MTU3NCwidXNlcl9pZCI6NCwidXNlcl9wb3N0Ijoic3R1ZGVudCJ9.m97dLqUqCxMewJD1vnSqVUUT9g3-fbsVJRWCkUhUtjk"; // Ваш токен
			const labToken = "ajkdfnshdabvpdhifvndsfjhlnfsjdlfkjsdfsdfsdf";

			const response = await axios.get("http://localhost:8001/lab1a/variant", {
				headers: {
					Authorization: `Bearer ${token}`,
					"lab-token": labToken,
				},
				params: {
					laboratory_id: 1,
				},
			});

			console.log("Ответ от сервера:", response.data);

			// Извлекаем данные
			const extractedData = response.data.variant || response.data; // Работаем с "variant"
			setData([extractedData]); // Преобразуем в массив для использования .map
		} catch (err: any) {
			setError(err.message || "Ошибка при выполнении запроса");
		} finally {
			setLoading(false);
		}
	};

  useEffect(() => {
    fetchData();
  }, []);

  return (
    <div style={{ padding: "20px" }}>
      <h1>Generated Lab1A Variance</h1>

      {loading && <p>Загрузка данных...</p>}
      {error && <p style={{ color: "red" }}>{error}</p>}

      <div>
        {data.map((item) => (
          <div key={item.number} style={{ marginBottom: "20px" }}>
            <h2>Number: {item.number}</h2>
            <div>
              <h3>Matrices:</h3>
              {item.variance.matrices.map((matrix, idx) => (
                <div key={idx} style={{ marginBottom: "15px" }}>
                  <p>Matrix {idx + 1}:</p>
                  <table border={1} style={{ borderCollapse: "collapse" }}>
                    <tbody>
                      {matrix.map((row, rowIndex) => (
                        <tr key={rowIndex}>
                          {row.map((value, colIndex) => (
                            <td key={colIndex} style={{ padding: "5px" }}>
                              {value}
                            </td>
                          ))}
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              ))}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Lab1aVariance;
