import React, { useState } from 'react';

const Lab1AResult = () => {
  const [elements, setElements] = useState(['', '', '']);
  const [selectedElement, setSelectedElement] = useState(null);

  const handleChange = (index, value) => {
    const newElements = [...elements];
    newElements[index] = value;
    setElements(newElements);
  };

  const handleSelect = (value) => {
    setSelectedElement(value);
  };

  return (
    <div style={styles.container}>
      <h2>Введите 3 элемента</h2>
      <div style={styles.inputContainer}>
        {elements.map((el, index) => (
          <input
            key={index}
            type="text"
            value={el}
            onChange={(e) => handleChange(index, e.target.value)}
            placeholder={`Элемент ${index + 1}`}
            style={styles.input}
          />
        ))}
      </div>

      {elements.every((el) => el) && (
        <div style={styles.selectContainer}>
          <h3>Выберите элемент:</h3>
          <div style={styles.buttonGroup}>
            {elements.map((el, index) => (
              <button
                key={index}
                onClick={() => handleSelect(el)}
                style={{
                  ...styles.button,
                  backgroundColor: selectedElement === el ? '#4CAF50' : '#2196F3',
                  color: 'white',
                }}
              >
                {el}
              </button>
            ))}
          </div>
        </div>
      )}

      {selectedElement && (
        <div style={styles.selectedElementContainer}>
          <h3>Вы выбрали: {selectedElement}</h3>
        </div>
      )}
    </div>
  );
};

const styles = {
  container: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    padding: '20px',
    fontFamily: 'Arial, sans-serif',
  },
  inputContainer: {
    display: 'flex',
    gap: '10px',
    marginBottom: '20px',
  },
  input: {
    padding: '10px',
    fontSize: '16px',
    borderRadius: '5px',
    border: '1px solid #ccc',
    width: '150px',
  },
  selectContainer: {
    marginTop: '20px',
  },
  buttonGroup: {
    display: 'flex',
    gap: '10px',
  },
  button: {
    padding: '10px 20px',
    fontSize: '16px',
    borderRadius: '5px',
    border: 'none',
    cursor: 'pointer',
    transition: 'background-color 0.3s',
  },
  selectedElementContainer: {
    marginTop: '20px',
    fontSize: '18px',
    fontWeight: 'bold',
  },
};

export default Lab1AResult;
